import React, { useState } from 'react';
import axios from 'axios';

// Replace with your actual API key
const GOOGLE_MAPS_API_KEY = 'AIzaSyD5jBbxTqK1YdsFRg-ebZK-0s1EXvXpZq0';

const StateSearch = () => {
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResult, setSearchResult] = useState(null);
  const [error, setError] = useState(null);

  const handleSearch = async (e) => {
    const term = e.target.value;
    setSearchTerm(term);
    
    if (term.length >= 2) {
      try {
        const response = await axios.get(`http://127.0.0.1:5000/api/search`, {
          params: { state: term }
        });
        setSearchResult(response.data);
        setError(null);
      } catch (err) {
        console.error('Error:', err);
        setError('Error fetching data');
        setSearchResult(null);
      }
    } else {
      setSearchResult(null);
      setError(null);
    }
  };

  return (
    <div className="max-w-4xl mx-auto p-6">
      <h2 className="text-2xl font-bold mb-6">State Search</h2>
      <div className="search-container mb-6">
        <input
          type="text"
          id="stateSearch"
          placeholder="Search for a state..."
          value={searchTerm}
          onChange={handleSearch}
          className="w-full p-3 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
        />
      </div>
      <div id="searchResults">
        {error && <div className="text-red-600 p-4 bg-red-100 rounded-lg">{error}</div>}
        {searchResult && !error && (
          <div className="bg-white rounded-lg shadow-lg p-6">
            <h2 className="text-2xl font-bold mb-4">{searchResult.name}</h2>
            
            {/* Key Information */}
            <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-6">
              <div className="p-4 bg-gray-50 rounded-lg">
                <p className="text-gray-600">Winner</p>
                <p className="font-bold">{searchResult.winner}</p>
              </div>
              <div className="p-4 bg-gray-50 rounded-lg">
                <p className="text-gray-600">Margin</p>
                <p className="font-bold">{searchResult.margin}%</p>
              </div>
              <div className="p-4 bg-gray-50 rounded-lg">
                <p className="text-gray-600">2024 Betting Odds</p>
                <p className="font-bold">{searchResult.odds}</p>
              </div>
            </div>
            
            {/* Voting Percentages */}
            <div className="mb-8">
              <h3 className="text-xl font-semibold mb-4">Voting Percentages</h3>
              <div className="space-y-4">
                <div>
                  <div className="flex justify-between mb-1">
                    <span className="font-medium">Democratic</span>
                    <span>{searchResult.dem_percentage}</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2.5">
                    <div 
                      className="bg-blue-600 h-2.5 rounded-full transition-all duration-300"
                      style={{ width: `${searchResult.dem_percentage}` }}
                    ></div>
                  </div>
                </div>
                
                <div>
                  <div className="flex justify-between mb-1">
                    <span className="font-medium">Republican</span>
                    <span>{searchResult.rep_percentage}</span>
                  </div>
                  <div className="w-full bg-gray-200 rounded-full h-2.5">
                    <div 
                      className="bg-red-600 h-2.5 rounded-full transition-all duration-300"
                      style={{ width: `${searchResult.rep_percentage}` }}
                    ></div>
                  </div>
                </div>
              </div>
            </div>

            {/* Google Maps */}
            <div>
              <h3 className="text-xl font-semibold mb-4">State Map</h3>
              <div className="rounded-lg overflow-hidden">
                <iframe
                  width="100%"
                  height="450"
                  className="border-0"
                  loading="lazy"
                  allowFullScreen
                  referrerPolicy="no-referrer-when-downgrade"
                  src={`https://www.google.com/maps/embed/v1/place?key=${GOOGLE_MAPS_API_KEY}&q=${searchResult.name}+state+USA&zoom=6`}
                ></iframe>
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default StateSearch;
