import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './BettingOdds2024.css';

function BettingOdds2024() {
  const [bettingOdds2024, setBettingOdds2024] = useState([]);

  // Convert American odds to probability percentage
  const oddsToPercentage = (americanOdds) => {
    if (americanOdds > 0) {
      return (100 / (americanOdds + 100) * 100).toFixed(1);
    }
    return (Math.abs(americanOdds) / (Math.abs(americanOdds) + 100) * 100).toFixed(1);
  };

  useEffect(() => {
    axios
      .get('http://127.0.0.1:5000/betting-odds-2024')
      .then((response) => {
        // Sort data by Republican win probability and normalize probabilities
        const processedData = response.data.map(item => {
          const repProb = parseFloat(oddsToPercentage(item.republican_odds));
          const demProb = parseFloat(oddsToPercentage(item.democrat_odds));
          const total = repProb + demProb;
          
          return {
            ...item,
            republican_probability: ((repProb / total) * 100).toFixed(1),
            democrat_probability: ((demProb / total) * 100).toFixed(1),
          };
        }).sort((a, b) => b.republican_probability - a.republican_probability);
        
        setBettingOdds2024(processedData);
      })
      .catch((error) => {
        console.error('Error fetching 2024 betting odds:', error);
      });
  }, []);

  return (
    <div className="betting-odds-2024">
      <h2>2024 Presidential Election Betting Market</h2>
      <p className="odds-disclaimer">*Implied probabilities from betting odds. Numbers may not add to 100% due to bookmaker margins.</p>
      <table>
        <thead>
          <tr>
            <th>State</th>
            <th>Republican</th>
            <th>Democrat</th>
          </tr>
        </thead>
        <tbody>
          {bettingOdds2024.map((item, index) => (
            <tr key={index}>
              <td>{item.state}</td>
              <td className="probability-cell">
                <div className="probability-bar">
                  <div 
                    className="republican-bar"
                    style={{ width: `${item.republican_probability}%` }}
                  >
                    <span className="odds-text">
                      {item.republican_probability}% ({item.republican_odds > 0 ? '+' : ''}{item.republican_odds})
                    </span>
                  </div>
                </div>
              </td>
              <td className="probability-cell">
                <div className="probability-bar">
                  <div 
                    className="democrat-bar"
                    style={{ width: `${item.democrat_probability}%` }}
                  >
                    <span className="odds-text">
                      {item.democrat_probability}% ({item.democrat_odds > 0 ? '+' : ''}{item.democrat_odds})
                    </span>
                  </div>
                </div>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default BettingOdds2024;
