import React, { useEffect, useState } from "react";

const DataTable = () => {
  const [data, setData] = useState([]);
  const [newRow, setNewRow] = useState({});
  const [editingIndex, setEditingIndex] = useState(null);

  useEffect(() => {
    fetch("http://127.0.0.1:5000/data")
      .then((response) => response.json())
      .then((data) => setData(data))
      .catch((error) => console.error("Error fetching data:", error));
  }, []);

  const handleAddRow = () => {
    fetch("http://127.0.0.1:5000/data", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify(newRow),
    })
      .then((response) => response.json())
      .then(() => {
        setData([...data, newRow]);
        setNewRow({});
      })
      .catch((error) => console.error("Error adding row:", error));
  };

  const handleDelete = (index) => {
    fetch(`http://127.0.0.1:5000/data/${index}`, { method: "DELETE" })
      .then(() => setData(data.filter((_, i) => i !== index)))
      .catch((error) => console.error("Error deleting row:", error));
  };

  const handleEdit = (index) => {
    const rowToEdit = data[index];
    setNewRow(rowToEdit);
    setEditingIndex(index);
  };

  const handleSaveEdit = () => {
    fetch("http://127.0.0.1:5000/data", {
      method: "PUT",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ index: editingIndex, row: newRow }),
    })
      .then((response) => response.json())
      .then(() => {
        const updatedData = [...data];
        updatedData[editingIndex] = newRow;
        setData(updatedData);
        setNewRow({});
        setEditingIndex(null);
      })
      .catch((error) => console.error("Error editing row:", error));
  };

  return (
    <div>
      <h2>Data Table</h2>

      {/* Add New Row */}
      <div>
        <h3>{editingIndex !== null ? "Edit Row" : "Add New Row"}</h3>
        {Object.keys(data[0] || {}).map((key) => (
          <input
            key={key}
            placeholder={key}
            value={newRow[key] || ""}
            onChange={(e) =>
              setNewRow({ ...newRow, [key]: e.target.value })
            }
          />
        ))}
        {editingIndex !== null ? (
          <button onClick={handleSaveEdit}>Save Edit</button>
        ) : (
          <button onClick={handleAddRow}>Add Row</button>
        )}
      </div>

      {/* Data Table */}
      <table>
        <thead>
          <tr>
            {data.length > 0 &&
              Object.keys(data[0]).map((key) => <th key={key}>{key}</th>)}
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((row, index) => (
            <tr key={index}>
              {Object.values(row).map((value, i) => (
                <td key={i}>{value}</td>
              ))}
              <td>
                <button onClick={() => handleEdit(index)}>Edit</button>
                <button onClick={() => handleDelete(index)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DataTable;
