import React, { useState, useEffect } from "react";
import axios from "axios";
import './App.css';

function App() {
  const [googleEarthImage, setGoogleEarthImage] = useState(null);
  const [pollingData, setPollingData] = useState([]);
  const [bettingOdds, setBettingOdds] = useState([]);

  // Fetch Google Earth Image
  const fetchGoogleEarthImage = async (location) => {
    const response = await axios.get(`http://localhost:5000/google-earth?location=${location}`);
    setGoogleEarthImage(response.data.image_url);
  };

  // Fetch Polling Data
  const fetchPollingData = async () => {
    const response = await axios.get("http://localhost:5000/polling-data");
    setPollingData(response.data);
  };

  // Fetch Betting Odds
  const fetchBettingOdds = async () => {
    try {
      const response = await axios.get("http://localhost:5000/betting-odds");
      setBettingOdds(response.data);
    } catch (error) {
      console.error("Error fetching betting odds:", error);
      if (error.response) {
        // Server responded with a status code outside of 2xx
        alert(`Error: ${error.response.data.error}`);
      } else if (error.request) {
        // No response from the server
        alert("No response from the server. Please try again later.");
      } else {
        // Something else went wrong
        alert("An unexpected error occurred. Please try again later.");
      }
    }
  };
  

  useEffect(() => {
    fetchPollingData();
    fetchBettingOdds();
    fetchGoogleEarthImage('California');
  }, []);

  return (
    <div className="App">
      <h1>2024 U.S. Presidential Election</h1>

      <div className="google-earth">
        <h2>Google Earth Image</h2>
        {googleEarthImage ? (
          <img src={googleEarthImage} alt="Location" />
        ) : (
          <p>Loading...</p>
        )}
      </div>

      <div className="polling-data">
        <h2>Polling Data</h2>
        <ul>
          {pollingData.map((item, index) => (
            <li key={index}>
              {item.state}: Candidate A - {item.candidate_a}%, Candidate B - {item.candidate_b}%
            </li>
          ))}
        </ul>
      </div>

      <div className="betting-odds">
        <h2>Betting Odds</h2>
        <ul>
          {bettingOdds.map((item, index) => (
            <li key={index}>
              {item.candidate}: {item.odds} odds
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
